mapper.select:
select * from member
where id=#{id}
and password=#{password}

mapper.update:
update member
set password = #{password}, name = #{name}, phone = #{phone}
where id = #{id}

mapper.select:
select exists(
select 1
from
stockLike
where id=#{id} and srtnCd=#{srtnCd}
)

mapper.delete:
delete from stockLike
where id=#{id} and
srtnCd=#{srtnCd}
