mapper.select:
select*
from(
select @rownum:=@rownum+1 rownum, b.*
from board b,(select
@rownum:=0) as tmp
order by bno desc
) as
boardlist

where rownum > (#{pageNum}-1)*#{amount} and rownum <=#{pageNum}*#{amount}

mapper.update:
update board
set cnt = cnt+1
where bno = #{bno}

update board
set title =#{title} , content =
#{content}
where bno = #{bno}
