mapper.insert:
INSERT INTO stockInfo (itmsNm, mrktCtg, clpr, fltRt, mkp, basDt, hipr, lopr, trqu, mrktTotAmt,srtnCd) VALUES
(#{item.itmsNm}, #{item.mrktCtg}, #{item.clpr},#{item.fltRt},#{item.mkp},#{item.basDt}, #{item.hipr},#{item.lopr},#{item.trqu},#{item.mrktTotAmt},#{item.srtnCd})
ON DUPLICATE KEY UPDATE itmsNm=values(itmsNm), mrktCtg=values(mrktCtg),clpr=values(clpr),fltRt=values(fltRt), mkp=values(mkp),basDt=values(basDt),hipr=values(hipr),lopr=values(lopr),trqu=values(trqu),mrktTotAmt=values(mrktTotAmt);

mapper.select:
select *
from (
select @rownum:=@rownum+1 rownum, s.*
from stockInfo
s,(select @rownum:=0) as tmp
where itmsNm like concat('%',#{search},'%')
) as searchlist

where rownum>(#{pageNum}-1)*#{amount} and rownum <=#{pageNum}*#{amount}
